div.script:
  function validateleaveForm(event) {
    const selected_type=event.target.value;
    if (selected_type=='day') {
      custom_hours1=document.getElementsById("custom_hours1")
      custom_hours1.setAttribute("type", "hidden");
      custom_hours2=document.getElementsById("custom_hours2")
      custom_hours2.setAttribute("type", "hidden"); 
      half_day1=document.getElementsById("half_day1")
      half_day1.setAttribute("type", "hidden");
      half_day2=document.getElementsById("half_day2")
      half_day2.setAttribute("type", "hidden");
    } 

    if (selected_type=='day') {
      custom_hours1=document.getElementsById("custom_hours1")
      custom_hours1.setAttribute("type", "hidden");
      custom_hours2=document.getElementsById("custom_hours2")
      custom_hours2.setAttribute("type", "hidden"); 
      day_1=document.getElementsById("day_1")
      day_1.setAttribute("type", "hidden");
      day_2=document.getElementsById("day_2")
      day_2.setAttribute("type", "hidden");
    } 
   if (selected_type=='day') {
      day_1=document.getElementsById("day_1")
      day_1.setAttribute("type", "hidden");
      day_2=document.getElementsById("day_2")
      day_2.setAttribute("type", "hidden"); 
      half_day1=document.getElementsById("half_day1")
      half_day1.setAttribute("type", "hidden");
      half_day2=document.getElementsById("half_day2")
      half_day2.setAttribute("type", "hidden");
    } 


   }
  window.addEventListener('DOMContentLoaded', (event) => {
     [...document.getElementsById("leavecategory")].map(el=>{el.addEventListener("change",validateleaveForm)}); 

  });

  $(function () {

    var leavecategoryval = $( "#leavecategory" ).val();
    if ( leavecategoryval === "day" ) {
        $("#custom_hours1").hide();
        $("#custom_hours2").hide();
        $("#half_day1").hide();
        $("#half_day2").hide();
       $("#day_1").show();
        $("#day_2").show();
      }
    if ( leavecategoryval === "half_day" ) {
        $("#custom_hours1").hide();
        $("#custom_hours2").hide();
        $("#day_1").hide();
        $("#day_2").hide();
        $("#half_day1").show();
        $("#half_day2").show();

      }
   if ( leavecategoryval === "hours" ) {
        $("#half_day1").hide();
        $("#half_day2").hide();
        $("#day_1").hide();
        $("#day_2").hide();
        $("#custom_hours1").show();
        $("#custom_hours2").show();
      }



});
  $(document).ready(function(){
  $("#leavecategory").change(function(){
    var leavecategoryval = $( "#leavecategory" ).val();
    if ( leavecategoryval === "day" ) {
        $("#custom_hours1").hide();
        $("#custom_hours2").hide();
        $("#half_day1").hide();
        $("#half_day2").hide();
       $("#day_1").show();
        $("#day_2").show();
      }
    if ( leavecategoryval === "half_day" ) {
        $("#custom_hours1").hide();
        $("#custom_hours2").hide();
        $("#day_1").hide();
        $("#day_2").hide();
        $("#half_day1").show();
        $("#half_day2").show();

      }
   if ( leavecategoryval === "hours" ) {
        $("#half_day1").hide();
        $("#half_day2").hide();
        $("#day_1").hide();
        $("#day_2").hide();
        $("#custom_hours1").show();
        $("#custom_hours2").show();
      }

  });

});
